package LoopConcept;

public class TablesMultiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          int a = 18;
          
          
		  for(int i = 1; i<=10 ; i++)
		  {
		    int	tables = a*i;
		    System.out.println(tables);
		  }
		 
	}

}
